import React, { useState, useEffect } from 'react';
import { StyleSheet, View, Text, FlatList, TouchableOpacity } from 'react-native';

import { globalStyles } from '../styles/globalStyles';
import Card from '../shared/Card';
import { fetchData } from '../apiClient';

export default function Community ({ navigation }) {
  const [communityDecks, setCommunityDecks] = useState(null);

  useEffect(() => {
    fetchData('decks')
      .then(decks => setCommunityDecks(decks))
  }, [])

  return (
    <View style={globalStyles.container}>
      <Text style={{ ...globalStyles.titleText, textAlign: 'center' }}>Shared by the Community</Text>
      <FlatList
        data={communityDecks}
        keyExtractor={item => item._id}
        renderItem={({ item }) => (
          <TouchableOpacity
            onPress={() => navigation.push('CommunityDeckDetails', { item })} /* the same than do navigation.navigate('ReviewDetails', item); */
          // onLongPress={() => {
          //   setPopUpDeleteDeck(true);
          //   setDeckToDelete(item.title);
          // }}
          >
            <Card>
              <Text style={globalStyles.titleText}>{item.title}</Text>
            </Card>
          </TouchableOpacity>
        )}
      />
    </View>
  )
}
