import React, { useState, useEffect } from 'react';
import { StyleSheet, View, Text, Button, Modal, TouchableWithoutFeedback, Keyboard } from 'react-native';

import { globalStyles } from '../styles/globalStyles';
import Card from '../shared/card';

export default function CommunityDeckDetails ({ navigation }) {
  const deck = navigation.getParam('item');

  return (
    <View style={globalStyles.container}>
      <Text style={{ ...globalStyles.titleText, textAlign: "center" }}>{deck.title}</Text>
      <Card>
        <Text>{deck.cards.length} cards</Text>
      </Card>
      <View style={{ flexDirection: 'row', justifyContent: "space-between", alignItems: 'center' }}>
        <Text
          onPress={() => navigation.push('DeckAllCards', { deck })}
          style={styles.text}
        >See all cards</Text>
      </View>
      <Button title='Download' onPress={() => navigation.push('CardDetails', { cardsToStudy: deck.cards.filter(card => card.nextReview < Date.now()), setNextReview, deckTitle: deck.title })} />
    </View >
  )
}

// Estos estilos se repiten en Home, son los del "+", puedo sin√≥ crear un componente para los "+"
const styles = StyleSheet.create({
  modalToggle: {
    marginTop: 20,
    marginBottom: 10,
    borderWidth: 1,
    borderColor: 'lightgrey',
    padding: 10,
    borderRadius: 30,
    alignSelf: 'center',
  },
  modalClose: {
    marginTop: 20,
    marginBottom: 10,
  },
  modalContent: {
    flex: 1,
  },
  text: {
    marginTop: 10,
    marginBottom: 10,
  }
});
