import React, { useState, useEffect } from 'react';
import { StyleSheet, View, Text, Button, Modal, TouchableWithoutFeedback, Keyboard, TouchableOpacity, Animated } from 'react-native';
import { MaterialIcons } from '@expo/vector-icons';
import { NavigationEvents } from 'react-navigation';


import { globalStyles } from '../styles/globalStyles';
import Card from '../shared/card';
import { fetchData } from '../apiClient';

import FontAwesome from 'react-native-vector-icons/FontAwesome';

export default function DeckDetails ({ navigation }) {
  const addCard = navigation.getParam('addCard');
  const editCard = navigation.getParam('editCard');
  const deleteCard = navigation.getParam('deleteCard');
  const setNextReview = navigation.getParam('setNextReview');
  const deck = navigation.getParam('item');
  const [deckLength, setDeckLength] = useState(deck.cards.length);
  const [shared, setShared] = useState(false);
  const [fadeMessage, setFadeMessage] = useState(false);

  const fadeValue = new Animated.Value(1);  // 1 because starts completly visible

  function shareDeck (deck) { // NO FUNCIONA BIEN NO MUESTRA BIEN EL MENSAJE DE QUE SE COMPARTIO
    if (!fadeMessage) {
      fetchData('decks', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(deck)
      });
      setFadeMessage(false);
      setFadeMessage(true);
    }
  }

  const showMessage = () => {
    Animated.timing(fadeValue, {
      toValue: 0, // 0 because after the 3000 ms it's not visible
      duration: 3000
    }).start();
  };

  return (
    <View style={globalStyles.container}>
      <NavigationEvents
        onDidFocus={() => setDeckLength(deck.cards.length)}   //update the deckLength
      />
      <Text style={{ ...globalStyles.titleText, textAlign: "center" }}>{deck.title}</Text>
      <Card>
        <Text>{deckLength} cards</Text>
      </Card>
      <View style={{ flexDirection: 'row', justifyContent: "space-between", alignItems: 'center' }}>
        <Text
          onPress={() => navigation.push('DeckAllCards', { editCard, deleteCard, deck })}
          style={styles.text}
        >See all cards</Text>
        <TouchableOpacity
          onPress={() => shareDeck(deck)}
        >
          <Text>
            <FontAwesome name='share' style={{ fontSize: 20, color: '#4F74FF' }} />
          Share
        </Text>
        </TouchableOpacity>
      </View>
      <Button title='Study' onPress={() => navigation.push('CardDetails', { cardsToStudy: deck.cards.filter(card => card.nextReview < Date.now()), setNextReview, deckTitle: deck.title })} />
      <MaterialIcons
        name='add'
        size={24}
        style={{ ...styles.modalToggle, ...styles.modalClose }}
        onPress={() => navigation.push('AddOrEditCard', { addCard, deckTitle: deck.title })}
      />
      {
        fadeMessage &&
        (
          showMessage(),
          <View style={globalStyles.fadeMessageContainer}>
            <Animated.View
              style={{
                opacity: fadeValue,
                ...globalStyles.fadeMessage,
              }}
            >
              <Text>Deck shared</Text>
            </Animated.View>
          </View>
        )
      }
    </View >
  )
}

// Estos estilos se repiten en Home, son los del "+", puedo sin√≥ crear un componente para los "+"
const styles = StyleSheet.create({
  modalToggle: {
    marginTop: 20,
    marginBottom: 10,
    borderWidth: 1,
    borderColor: 'lightgrey',
    padding: 10,
    borderRadius: 30,
    alignSelf: 'center',
  },
  modalClose: {
    marginTop: 20,
    marginBottom: 10,
  },
  modalContent: {
    flex: 1,
  },
  text: {
    marginTop: 10,
    marginBottom: 10,
  }
});
